string displayWord(const string& word, const vector<char>& guessedLetters) //Функция для показа текущего прогресса слова
{
	string display;

	for (char letter : word) 
	{
		display += (find(guessedLetters.begin(), guessedLetters.end(), letter) != guessedLetters.end()) ? letter : '_'; //Для отображения прогресса отгадывания слова
		display += ' ';
	}
	return display;
}

